      'bin/cli.js': `#!/usr/bin/env node

const { program } = require('commander');
const chalk = require('chalk');
const { version } = require('../package.json');

program
  .name('cli-tool')
  .description('A sample Node.js CLI tool')
  .version(version);

program
  .command('hello')
  .description('Say hello')
  .argument('<name>', 'name to greet')
  .option('-u, --uppercase', 'uppercase the output')
  .action((name, options) => {
    const greeting = \`Hello, \${name}!\`;
    console.log(chalk.blue(options.uppercase ? greeting.toUpperCase() : greeting));
  });

program
  .command('info')
  .description('Show system information')
  .action(() => {
    console.log(chalk.green('System Information:'));
    console.log(\`Node.js version: \${process.version}\`);
    console.log(\`Platform: \${process.platform}\`);
    console.log(\`Architecture: \${process.arch}\`);
  });

program.parse();
`,
