router.get('/list', (req, res) => {
  const templateList = Object.entries(templates).map(([id, template]) => ({
    id,
    name: template.name,
    description: template.description
  }));
  
  res.json(templateList);
});

router.post('/create/:templateId', async (req, res) => {
  try {
    const { templateId } = req.params;
    const { name } = req.body;
    
    if (!templates[templateId]) {
      return res.status(404).json({ error: 'Template not found' });
    }
    
    const template = templates[templateId];
    const workspaceId = uuidv4();
    const workspacePath = path.join(req.workspacesDir, workspaceId);
    
    await fs.ensureDir(workspacePath);
    
    for (const [filePath, content] of Object.entries(template.files)) {
      const fullPath = path.join(workspacePath, filePath);
      await fs.ensureFile(fullPath);
      
      if (typeof content === 'object') {
        // Handle package.json
        const packageJson = { ...content };
        if (name) {
          packageJson.name = name.toLowerCase().replace(/\s+/g, '-');
        }
        await fs.writeFile(fullPath, JSON.stringify(packageJson, null, 2));
      } else {
        await fs.writeFile(fullPath, content);
      }
    }
    
    res.json({
      workspaceId,
      name: name || template.name,
      template: template.name,
      message: 'Template created successfully'
    });
  } catch (error) {
    console.error('Error creating template:', error);
    res.status(500).json({ error: 'Failed to create template' });
  }
});

export default router;